import React , {useState} from "react";
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';
import DoneIcon from '@mui/icons-material/Done';

function Note(props) {
  const [contentEditable, setContentEditable] = useState(false)
  const [inputText, setInputText] = useState({title:"",content:""})
  function handleChange(event){
    event.target.name = 
    setInputText(prevValue =>{
      return {
        ...prevValue,
        [name]:value
      }
    })

  }
  return (
    <div className="note">
      <h1 onChange={handleChange} contentEditable = {contentEditable}>{props.title}</h1>
      <p onChange={handleChange}  contentEditable = {contentEditable}>{props.content}</p>
      {/* Edit has finished button */}
      {contentEditable?<button onClick={ ()=>{
        props.editItem(props.id,props.title,props.content)
        setContentEditable(false)
      }}> <DoneIcon></DoneIcon></button> :null}  

      {/* Delete item Button */}
      {contentEditable?null :<button onClick = {(e)=>{
        props.deleteItem(props.id)
      }}><DeleteIcon /></button>}

      {/* edit item Button */}
      {contentEditable? null :<button onClick={ ()=>{
        setContentEditable(true)
      }}><EditIcon></EditIcon> </button>}   
    </div>
  );
}

export default Note;
