import React , {useState} from "react";
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';
import DoneIcon from '@mui/icons-material/Done';

function Note(props) {
  const [contentEditable, setContentEditable] = useState(false)
  const [inputText, setInputText] = useState({title:"",content:""})
  function handleChange(event){
    var name = event.target.name 
    var value = event.target.value
    console.log(name)
    console.log(value)
    console.log("hello")
    setInputText(prevValue =>{
      return {
        ...prevValue,
        [name]:value
      }
    })

  }
  return (
    <div className="note">
    <form className="create-note" >
        {closed? <input onChange ={handleChange} name="title" placeholder="Title" value = {inputText.title}/>:null } 
        <textarea onClick={()=>{
          change(true)
        }} onChange ={handleChange} name="content" placeholder="Take a note..." rows= {closed? 3 :1 } value= {inputText.content} />

      </form>
      <input value = "hello" />
      <h1 onChange={handleChange} contentEditable = {contentEditable} name = "title" value = {inputText.title}>{props.title}</h1>
      <p onChange={handleChange}  contentEditable = {contentEditable} name = "content" value = {inputText.content}>{props.content}</p>
      {/* Edit has finished button */}
      {contentEditable?<button onClick={ ()=>{
        props.editItem(props.id,props.title,props.content)
        setContentEditable(false)
      }}> <DoneIcon></DoneIcon></button> :null}  

      {/* Delete item Button */}
      {contentEditable?null :<button onClick = {(e)=>{
        props.deleteItem(props.id)
      }}><DeleteIcon /></button>}

      {/* edit item Button */}
      {contentEditable? null :<button onClick={ ()=>{
        setContentEditable(true)
      }}><EditIcon></EditIcon> </button>}   
    </div>
  );
}

export default Note;
