import React, {useState , useEffect} from "react";
import Header from "./Header";
import Footer from "./Footer";
import Note from "./Note";
import CreateArea from "./CreateArea";
import DataFetching from "./DataFetching";
import axios from "axios"
// import NoteDB from "../server.js"


function App() {


  // const Data = [{title:"Test 1",content:"this is a test"},{title:"2nd test",content:"this is a test"}]
  // https://jsonplaceholder.typicode.com/posts
  const [items, setItems] = useState([])
  useEffect(() =>{
    axios.get("http://localhost:4000/api")
    .then(res =>{
        const data = res.data
        setItems(data)
    })
    .catch(err => {
      if (err.response) {
        console.log(err.response)
        // client received an error response (5xx, 4xx)
      } else if (err.request) {
        console.log(err.request)
        // client never received a response, or request never left
      } else {
        console.log(err)
        // anything else
      }
  })
},[items]
)

  function addItem(item){
    const sendPostRequest = async() => {
      try{
        const resp = await axios.post('http://localhost:4000/api/post',
      {
        title:item.title,
      content:item.content
      },
      {
        headers: { 'Content-Type': 'application/x-www-form-urlencoded' }
      });

      }catch(err){
        console.error(err)
      }
    }



  //   axios.post("http://localhost:4000/api/post",{
  //     title:item.title,
  //     content:item.content
  //   },headers)
  //   .then(res =>{
  //     console.log(res)
  //   })
  //   .catch(err => {
  //     if (err.response) {
  //       console.log(err.response.data)
  //       // client received an error response (5xx, 4xx)
  //     } else if (err.request) {
  //       console.log(err.request)
  //       // client never received a response, or request never left
  //     } else {
  //       console.log(err)
  //       // anything else
  //     }
  // })


    setItems(prevValue => {
      return [...prevValue , item ]
    })
  }
  function deleteItem(id){
    setItems(prevItems => {
      return prevItems.filter((item,index)=>{
        return index !== id
      })
    })
  }

  function editItem(id,title,content){
    setItems(prevItems =>{
      prevItems.map((item,index) =>{
        if (id === index){
          item.title = title
          item.content = content
        }
      })
      return prevItems
    })


  }
  return (
    <div>
      <Header />
      <DataFetching />
      <CreateArea addItem ={addItem} />
      {items.map((item,index) => {
        return <Note editItem= {editItem} deleteItem = {deleteItem} key = {index} id={index} title = {item.title} content = {item.content}> </Note>
      })}



      <Footer />
    </div>
  );
}

export default App;
