import React, {useState} from "react";
import Header from "./Header";
import Footer from "./Footer";
import Note from "./Note";
import CreateArea from "./CreateArea";
// import { ConnectionStates } from "mongoose";

function App() {


  const [items, setItems] = useState([{title:"This is",content:"a test"}])

  function addItem(item){
    setItems(prevValue => {
      return [...prevValue , item ]
    })
  }
  function deleteItem(id){
    setItems(prevItems => {
      return prevItems.filter((item,index)=>{
        return index !== id
      })
    })
  }
  function editItem(id,title,content){
    console.log(id)
    console.log(title)
    console.log(content)
    setItems(prevItems =>{
      prevItems.map((item,index) =>{
        if (id === index){
          item.title = title
          item.content = content
        }
      })

      console.log(prevItems)
      return prevItems
    })


  }
  return (
    <div>
      <Header />
      <CreateArea addItem ={addItem} />
      {items.map((item,index) => {
        return <Note editItem= {editItem} deleteItem = {deleteItem} key = {index} id={index} title = {item.title} content = {item.content}> </Note>
      })}



      <Footer />
    </div>
  );
}

export default App;
