import React,{useState} from "react";

function CreateArea(props) {
  const [inputText, setInputText] = useState({title:"",content:""})


  function handleChange(event){
    // const [name,value] = event.target
    const name = event.target.name
    const value = event.target.value
    console.log(value)
    // setInputText(prevValue =>{
    //   return {
    //     ...prevValue,
    //     [name]:value
    //   }
    // })


      setInputText(prevValue=>{
        if(name==="title")
        prevValue.title = value
        console.log(prevValue)
        return prevValue
      })

      setInputText(prevValue=>{
        var newValue = prevValue
         newValue.content = value
        return newValue
      })


  }
function submiteNote(e){
      props.addItem(inputText)
      setInputText({title:"",content:""})
      e.preventDefault()
}

  return (
    <div>
      <form  >
        <input onChange ={handleChange} name="title" placeholder="Title" value = {inputText.title}/>
        <textarea onChange ={handleChange} name="content" placeholder="Take a note..." rows="3" value= {inputText.content} />
        <button onClick = {submiteNote} >Add</button>
      </form>
    </div>
  );
}

export default CreateArea;
